      color: var(--toast-error-color);
    }
    .close:hover {
      color: color.adjust($toast-error-color, $lightness: -10%);
    }
  }

  &.info {
    background: var(--toast-info-color-light);
    border: 1px solid var(--toast-info-color);
    border-left: 5px solid var(--toast-info-color);

    p,
    a,
    fa-icon {
      display: inline-block;
      color: var(--toast-info-color);
    }
    .close:hover {
      color: color.adjust($toast-info-color, $lightness: -10%);
    }
  }

  &.warning {
    background: var(--toast-warning-color-light);
    border: 1px solid var(--toast-warning-color);
    border-left: 5px solid var(--toast-warning-color);

    p,
    a,
    fa-icon {
      display: inline-block;
      color: var(--toast-warning-color);
    }
    .close:hover {
      color: color.adjust($toast-warning-color, $lightness: -10%);
    }
  }
}

/***************************************************************
Media query to hide hover effects for devices 
that do not support them, like touch displays
**************************************************************/
@media (hover: hover) {
  a:hover {
    font-weight: var(--font-weight-medium);
  }
}

