:host {
  display: inline-flex;
  align-items: center;
  box-sizing: border-box;
}

.badge {
  padding: 0.7em 1em;
  border-radius: var(--badge-radius);
  background-color: var(--not-emphrazed-color);
  display: flex;
  align-items: center;
  column-gap: 10px;

  p {
    font-weight: var(--font-weight-medium);
    color: var(--fa-icon-color);
    margin: 0px;
    padding: 0px;
    line-height: 10px;
  }

  &.pos-absolute {
    position: absolute;
    top: -5px;
    right: -5px;
    border-radius: 50%;
    width: 10px;
    height: 10px;
    line-height: 9px;
    background-color: var(--modified-data-color);
    color: var(--fa-icon-color);
    font-weight: var(--font-weight-medium);
    padding: 2px;
    z-index: 1500;

    &.xs {
      top: 1px;
      padding: 0px;
    }

    &.s {
      padding: 5px;
      display: flex;
      justify-content: center;
    }

    &.event {
      top: -4px;
      right: -1px;
      padding: 0px;
    }
  }

  &.success {
    background-color: var(--job-successful-color);
  }

  &.failed {
    background-color: var(--job-failed-color);
  }

  &.pending {
    background-color: var(--job-pending-color);
  }

  &.running {
    background-color: var(--job-running-color);
  }

  &.changes {
    background-color: var(--modified-data-color);
    color: white;
    border-radius: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
    width: calc(var(--btn-height) - 15px);
    height: calc(var(--btn-height) - 15px);
    box-sizing: border-box;
    padding: 0.4em;
    position: relative;
    margin: auto;
  }

  /** task states ***/
  &.current {
    background-color: var(--current-color);
    p {
      font-weight: var(--font-weight-semibold);
      text-transform: uppercase;
      color: var(--current-color-dark);
    }
  }

  &.urgent {
    background-color: var(--urgent-color);
    border: 1px solid white;
    p {
      font-weight: var(--font-weight-semibold);
      text-transform: uppercase;
      color: var(--urgent-color-dark);
    }
  }

  &.frozen {
    background-color: var(--frozen-color);
    p {
      font-weight: var(--font-weight-semibold);
      text-transform: uppercase;
      color: var(--frozen-color-dark);
    }
  }

  &.manual {
    background-color: var(--manual-color);
    p {
      font-weight: var(--font-weight-semibold);
      text-transform: uppercase;
      color: var(--manual-color-dark);
    }
  }

  &.free {
    background-color: var(--free-color);
    border: 1px solid var(--border-color);

    p {
      font-weight: var(--font-weight-semibold);
      text-transform: uppercase;
      color: var(--free-color-dark);
    }

    fa-icon {
      color: var(--free-color-dark);
    }
  }
}

.disabled {
  opacity: 0.3;
  cursor: not-allowed;
}

/***************************************************************
Media query to hide hover effects for devices 
that do not support them, like touch displays
**************************************************************/
@media (hover: hover) {
  .badge {
    &:hover,
    &.active {
      &:not([disabled]) {
        filter: brightness(85%);
      }
    }
  }
}

