  display: table;
  width: 100%;
}

.text-wrapper {
  display: table-cell;
  vertical-align: middle;
}

.centered {
  font-family: sans-serif;
  font-size: 1.1em;
  text-align: center;

  p {
    padding-bottom: 0px;
    margin-bottom: 0px;
  }
}

input[type='file'] {
  display: none;
}

.upload-btn {
  display: inline-block;
  padding: var(--btn-padding);
  height: var(--btn-height);
  line-height: var(--btn-height);
  background-color: var(--accent-color);
  color: var(--fa-icon-color);
  border-radius: var(--border-radius);
  cursor: pointer;
  font-size: 0.9em;
}

.file-item {
  border: 1px solid var(--border-color);
  margin: 5px 0px;
  display: flex;
  flex-direction: row;
  justify-content: space-between;
  align-items: center;

  .left,
  .right {
    display: flex;
    flex-direction: row;
  }

  p {
    padding: 10px;
    margin: 0px;
  }

  span {
    padding: 10px;
  }

  .left {
    fa-icon {
      color: var(--text-color);
    }
    span {
      background: #efefef;
    }
  }

  .right {
    cursor: pointer;

    span {
      padding: var(--btn-padding);
    }

    fa-icon {
      color: var(--text-color);

      &:hover:not([disabled]) {
        color: var(--delete-color);
      }
    }
  }
}

/************************************
 Ripple
**************************************/

.ripple {
  background-position: center;
  transition: background 0.8s;

  &:hover:not([disabled]) {
    background-image: var(--darken-gradient);
  }

  &:active:not([disabled]) {
    background-color: var(--accent-color-dark);
  }
}

