/********************
CSS Variables -> --accent-color, --fa-icon-color for changing theme
********************/

:host {
  display: inline-block;
}

button {
  position: relative;
  height: var(--btn-height);
  line-height: var(--btn-height);
  padding: var(--btn-padding);
  border: var(--border-width);
  border-radius: var(--border-radius);
  font-weight: var(--font-weight-light);
  font-size: var(--font-size);
  transition: background 400ms;
  background: var(--accent-color);
  color: var(--fa-icon-color);
  cursor: pointer;
  display: flex;
  justify-content: center;
  align-items: center;
  box-sizing: border-box;

  fa-icon {
    color: var(--fa-icon-color);
  }

  span {
    color: var(--fa-icon-color);
    margin: 0px 5px;
  }
}

/************************************
 Ripple
**************************************/

.ripple {
  background-position: center;
  transition: background 0.8s;

  &.active,
  &:active:not([disabled]) {
    background-color: var(--accent-color-dark);
  }
}

/************************************
 Delete
**************************************/
.delete {
  background: var(--delete-color);

  &.active,
  &:active:not([disabled]) {
    background-color: var(--delete-color-dark);
  }
}

/************************************
 Not emphrazed
**************************************/
.not-emphrazed {
  background: var(--not-emphrazed-color);

  &.active,
  &:active:not([disabled]) {
    background-color: var(--not-emphrazed-color-dark);
  }
}

.navbar-btn {
  border-radius: 5px;
  height: calc(var(--btn-height) - 5px);
  line-height: calc(var(--btn-height) - 5px);
  padding: var(--btn-small-padding);
}

/************************************
Disabled
**************************************/
button:disabled,
button[disabled] {
  opacity: 0.5;
  background-size: 0% 100%;
  cursor: not-allowed;
}

/***************************************************************
Media query to hide hover effects for devices 
that do not support them, like touch displays
**************************************************************/
@media (hover: hover) {
  .ripple {
    &:hover:not([disabled]) {
      background-image: var(--darken-gradient);
    }
  }

  button:disabled,
  button[disabled] {
    &:hover::before,
    &:hover::after {
      width: 0%;
      height: 0%;
      border-color: transparent;
    }
  }
}

