import { CommonModule } from '@angular/common';
import { Component, Input } from '@angular/core';

/**
 * Component to create and show  table
 *
 * @howToUse
 * ```
 * <table-component>
 *     <div header>
 *         ...
 *     </div>
 *     <div content>
 *         ...
 *     </div>
 * </table-component>
 */
@Component({
  selector: 'table-component',
  standalone: true,
  imports: [CommonModule],
  templateUrl: './table.component.html',
  styleUrls: ['./table.component.scss'],
})
export class TableComponent {
  @Input() scrollbarWidth = 0;
  @Input() classStyle?: string;
}

styleUrlsData.data:
:host {
  display: inline-block;
  position: relative;
  width: 100%;
}

/***************************
 Table
**************************/

.table-container {
  position: relative;
  display: flex-inline;
  flex-direction: row;
  margin: 5px 5px;
  border: 1px solid var(--border-color);
  min-height: 50px;

  ::ng-deep .text-left {
    text-align: left;
  }

  ::ng-deep .ellipsis {
    overflow: hidden;
    text-overflow: ellipsis;
    display: block;
    height: var(--row-height);
    white-space: nowrap;
  }

  ::ng-deep .search-input {
    margin: 0.5em 0.5em;
    height: 35px;
    text-align: center;
    min-width: 60px;
    border: none;
    width: 100%;
    border-bottom: 1px solid var(--border-color);

    &:focus {
      border: none;
      border-bottom: var(--border-width) solid var(--accent-color);
    }
  }
}

/***************************
Table Header
**************************/
.table-header {
  font-weight: var(--font-weight-normal);
  font-size: 0.8em;
  position: relative;
  top: 0;
  left: 0;
  border-bottom: 1px solid #aaa;

  div {
    width: 100%;
  }

  ::ng-deep .flex-column {
    & > div {
      border-right: 1px solid var(--border-color);
      cursor: pointer;
    }

    .flex-item {
      flex: 1 1 0;
    }
  }

  ::ng-deep .sort {
    display: flex;
    flex-direction: row;
    align-items: center;
    justify-content: center;
    gap: 5px;
    width: 100%;

    h2 {
      margin: 0em;
      line-height: var(--btn-height);
      font-size: 1.15em;
      color: white;
      box-sizing: border-box;
      // display: contents;
      max-width: 150px !important;
      font-weight: var(--font-weight-semibold);
      overflow: hidden;
      text-overflow: ellipsis;
      white-space: nowrap;
    }

    &.withSelect {
      width: calc(100% - 40px);
    }
  }

  ::ng-deep h4 {
    color: white;
    padding: 0.5em;
  }

  ::ng-deep .flex-row {
    display: flex;
    flex-direction: row;
    position: relative;
    top: 0;
    left: 0;

    & > div {
      box-sizing: border-box;
      flex-basis: 100%;
      text-align: center;
      text-overflow: ellipsis;
      display: flex;
      justify-content: space-between;
      flex-direction: row;

      span {
        width: 10px;
      }

      &.overflow {
        overflow: hidden;
        height: var(--btn-height);
      }

      &.center {
        justify-content: center;
      }
    }

    .flex-item {
      flex: 1 1 0;
      width: 0;
      // overflow: hidden;
    }

    &:first-of-type {
      background-color: var(--accent-color);
    }

    &:last-of-type {
      border-bottom: 1px solid var(--border-color);

      & > div {
        border-right: 1px solid var(--border-color);

        &:last-of-type {
          border-right: none;
        }
      }
    }
  }
}

/***************************
Table Content
**************************/
.table-content {
  display: flex;
  flex-direction: column;
  position: relative;
  overflow-y: auto;
  height: 100%;

  ::ng-deep .table-row,
  ::ng-deep .table-row-collapse {
    position: relative;
    display: flex;
    flex-direction: row;
    box-sizing: border-box;
    border-bottom: 1px solid var(--border-color);
    cursor: pointer;

    &:active:not([disabled]),
    &.active {
      background-image: linear-gradient(
        var(--row-highlight-color),
        var(--row-highlight-color)
      ) !important;
      color: var(--accent-color);
    }

    &.no-hover {
      pointer-events: none;
      cursor: default;
    }

    & > div {
      flex-basis: 100%;
      border-right: 1px solid var(--border-color);
      box-sizing: border-box;
      height: var(--row-height);
      line-height: var(--row-height);
      text-overflow: ellipsis;
      overflow: hidden;
      padding: 0em 0.5em;
      line-height: 45px;
      font-size: 0.9em;
      white-space: nowrap;

      span {
        font-weight: var(--font-weight-light);
      }

      &:last-of-type {
        border-right: 1px solid var(--border-color);
      }
    }

    /** collapsable redesign *****/
    .collapsible-table {
      width: 100%;

      .collapsible-header {
        width: 100%;
        border: none;
        margin: 0;
        padding: 0;
      }

      .collapsible-table-header {
        position: relative;
        display: flex;
        flex-direction: row;
        box-sizing: border-box;
        height: var(--btn-height);

        & > div {
          flex-basis: 100%;
          border-right: 1px solid var(--border-color);
          box-sizing: border-box;
          height: calc(var(--row-height) - 3px);
          line-height: calc(var(--row-height) - 3px);
          text-overflow: ellipsis;
          overflow: hidden;
          padding: 0em 0.5em;
          font-size: 0.9em;
          align-items: center;
          word-wrap: break-word;

          span {
            font-weight: var(--font-weight-light);
          }

          &:last-of-type {
            border-right: 1px solid var(--border-color);
          }
        }

        &.active {
          background-color: var(--row-highlight-color);
          color: var(--accent-color);
          fa-icon {
            color: var(--accent-color);
          }
        }
      }

      .collapse-cell {
        display: grid;
        grid-template-columns: 0.5fr 3fr;
        justify-items: center;
        align-items: center;

        fa-icon {
          color: var(--text-color);
          width: var(--btn-height);
        }
      }

      .collapsible-content {
        margin: 0;
        background-color: white;
        border: none;
        border-top: 1px solid var(--border-color);

        p {
          margin: 0;
          padding: 0;
        }
      }
    }
  }
}

.table-container.inner-table {
  background-color: white;

  ::ng-deep .flex-row {
    &:first-of-type {
      background-color: white;

      h2 {
        color: var(--text-color);
        font-weight: var(--font-weight-medium);
      }

      fa-icon {
        color: var(--text-color);
      }
    }
  }
}

/************************************************
Media query to add hover only on device which support hover
*********************************************/

@media (hover: hover) {
  .table-container {
    .table-content {
      ::ng-deep .table-row {
        &:hover:not([disabled]):not(.inner-table) {
          background-image: linear-gradient(
            var(--row-highlight-color),
            var(--row-highlight-color)
          ) !important;
          color: var(--accent-color);
        }

        &:active:not([disabled]) {
          background-image: linear-gradient(
            var(--row-highlight-color),
            var(--row-highlight-color)
          ) !important;
          color: var(--accent-color);
        }
      }

      ::ng-deep .table-row-collapse {
        .collapsible-table-header {
          &:hover:not([disabled]):not(.inner-table) {
            background-image: linear-gradient(
              var(--row-highlight-color),
              var(--row-highlight-color)
            ) !important;
            color: var(--accent-color);

            ::ng-deep fa-icon {
              color: var(--accent-color) !important;
            }
          }

          &:active:not([disabled]) {
            background-image: linear-gradient(
              var(--row-highlight-color),
              var(--row-highlight-color)
            ) !important;
            color: var(--accent-color);

            ::ng-deep fa-icon {
              color: var(--accent-color) !important;
            }
          }
        }
      }
    }
  }

  .table-container.inner-table {
    .table-content {
      .collapsible-content.show {
        color: var(--text-color) !important;
      }

      ::ng-deep .table-row {
        // no hover on inner
        &.inner-table {
          color: var(--text-color) !important;
          background-color: white !important;
        }

        .collapsible-header {
          &:hover {
            background-image: linear-gradient(
              var(--row-highlight-color),
              var(--row-highlight-color)
            ) !important;
            color: var(--accent-color);

            ::ng-deep fa-icon {
              color: var(--accent-color) !important;
            }
          }
        }
      }
    }
  }
}

