/*
CSS Variables -> --accent-color for changing theme
*/
$toggle-btn-width: 65px;
$toggle-btn-height: calc(var(--btn-height) - 10px);
$slider-height: calc($toggle-btn-height / 2);

:host {
  position: relative;
  display: flex;
  align-items: center;
  box-sizing: border-box;
  height: var(--btn-height);
}

span {
  font-size: var(--font-size);
  margin: 0;
  margin-right: 5px;
  font-weight: var(--font-weight-normal);
}

.navbar {
  color: white;
}

.toggle {
  position: relative;
  width: $toggle-btn-width;
  height: $toggle-btn-height;
  overflow: hidden;
  border-radius: 100px;

  .slider,
  .layer {
    position: absolute;
    top: 0;
    right: 0;
    bottom: 0;
    left: 0;
  }

  .checkbox {
    position: relative;
    width: 100%;
    height: 100%;
    padding: 0;
    margin: 0;
    opacity: 0;
    cursor: pointer;
    z-index: 3;
  }

  .slider {
    z-index: 2;
  }

  .layer {
    width: auto;
    transition: 0.3s ease all;
    border: 1px solid var(--btn-border-color);
    z-index: 1;
    border-radius: 100px;

    &.active {
      border: 1px solid var(--accent-color);
    }
  }

  /* toggle 1 */
  .slider:before {
    content: ' ';
    position: absolute;
    left: 3px;
    bottom: 4px;
    width: 22px;
    height: 22px;
    color: var(--fa-icon-color);
    font-size: 1.1em;
    line-height: 0.7em;
    font-weight: var(--font-weight-medium);
    text-align: center;
    background-color: var(--toggle-btn-background);
    border-radius: 50%;
    transition: 0.3s cubic-bezier(0.18, 0.89, 0.35, 1.15) all;
  }

  .checkbox:checked + .slider:before {
    content: ' ';
    color: var(--accent-color);
    font-size: 1.1em;
    line-height: 0.9em;
    font-weight: var(--font-weight-medium);
    left: calc($toggle-btn-width - $toggle-btn-height + 4px);
    background-color: var(--fa-icon-color);
  }

  .checkbox:checked ~ .layer {
    background-color: var(--accent-color);
  }

  .checkbox:disabled {
    cursor: not-allowed;
  }

  .slider,
  .slider:before,
  .layer {
    transition: 0.3s ease all;
  }
}

.disabled {
  opacity: 0.5;
  cursor: not-allowed;
}

/************************************************
Media query to add hover only on device which support hover
*********************************************/

@media (hover: hover) {
  .toggle {
    &:hover {
      .layer {
        border: 1px solid var(--accent-color-light);
      }

      /* toggle 1 */
      .slider:before {
        color: var(--fa-icon-color);
        background-color: var(--accent-color);
      }

      .checkbox:checked + .slider:before {
        color: var(--accent-color);
        background-color: var(--fa-icon-color);
      }

      .checkbox:checked ~ .layer {
        background-color: var(--accent-color-light);
      }
    }
  }
}

